import { useState } from 'react';
import searchIcon from '../../../Images/search.svg';
import closeIcon from '../../../Images/close-icon.svg';

export default function HeaderSearch() {

    const [active, setActive] = useState(false);

    return (
        <>
            <img className="header__search-icon" src={searchIcon} onClick={()=>setActive(true)} alt='search icon'/>
            <div className={`search-popup ${active ? 'active' : ''}`}>
                <img src={closeIcon} alt="close icon" className="search-popup__close-icon" onClick={()=>setActive(false)}/>
                <input type="text" className="search-popup__search-field" />
            </div>
        </>
    )
}
