import { useState, useEffect } from "react";

export default function Slider({ slides }) {

  const [slideIndex, setSlideIndex] = useState(0);
  const [previousSlideIndex, setPreviousSlideIndex] = useState(0);

  let currentSlides = [slides[previousSlideIndex], slides[slideIndex]]

  function changeSlide() {
    const nextSlideIndex = slideIndex === slides.length - 1 ? 0 : slideIndex + 1;
    setPreviousSlideIndex(slideIndex);
    setSlideIndex(nextSlideIndex);
  }

  useEffect(() => {
    const interval = setInterval(() => {
      changeSlide();
    }, 100000);
    return () => {
      clearInterval(interval);
    };
  });

  return (
    <div className="slider">
      {currentSlides.map(slide => (
      <div className="slider_previous" key={previousSlide.id}>
      <img className="slider__img" src={previousSlide.url} alt="slide img" />
      <div className="slider__text-block">
        <h2 className="slider__title">{previousSlide.url}</h2>
        <h2 className="slider__subtitle">{previousSlide.url}</h2>
        <span className="slider__text">{previousSlide.url}</span>
      </div>
      <a className="slider__arrow-link" href={previousSlide.url}>
        {previousSlide.url}
      </a>
    </div>
      ))}

