import React, { useState } from "react";

function HeaderDropdown() {

    const [isActive, setIsActive] = useState(false);

    const headerMenu = [
        {
            title: 'О нас',
            link: '/'
        },
        {
            title: 'Портфолио',
            link: '/'
        },
        {
            title: 'Контакты',
            link: '/'
        },
        {
            title: 'Этапы проекта',
            link: '/'
        },
        {
            title: 'Заказать проект',
            link: '/'
        },
    ]


    return (
        <div onMouseLeave={() => setIsActive(false)}>
            <span
            className="header__dropdown-title"
            onMouseOver={() => setIsActive(true)}
            >Главная</span>
            <div className={`header__dropdown-menu ${isActive ? 'active':'' }`}>
                {headerMenu.map(i => (
                    <a className="header__dropdown-item" href={i.link}>sfvsdv</a>
                ))}
            </div>
        </div>
    )
}

export default HeaderDropdown;
