import { useState } from 'react';
import Logo from '../common/Logo/Logo'
import HeaderNavbar from './HeaderNavbar/HeaderNavbar';
import HeaderContact from './HeaderContact/HeaderContact';
import HeaderSearch from './HeaderSearch/HeaderSearch';
import SearchPopup from './HeaderSearch/HeaderSearch';

function Header() {
    const [searchActive, switchSearch] = useState(false);
    function handleMenuIsOpened() {
        switchSearch(!searchActive);
      }

    return (
        <header className='header'>
            <SearchPopup searchActive={searchActive} switchSearch={switchSearch}/>
            <div className='header__wrapper'>
                <Logo/>
                <HeaderNavbar />
                <HeaderContact/>
                <HeaderSearch switchSearch={switchSearch}/>
            </div>
        </header>
    )
}

export default Header;
