import { useState, useEffect } from "react";

export default function Slider({slides}) {

    const [slideIndex, setSlideIndex] = useState(0);
    let previousSlide = slides[slideIndex-1];
    let currentSlide = slides[slideIndex];

    function changeSlide() {
        console.log('slideIndex', slideIndex);
        console.log('slides.length', slides.length);
        const nextSlideIndex = slideIndex === slides.length -1 ? 0 : slideIndex + 1;
        console.log('nextSlideIndex', nextSlideIndex);
        setSlideIndex(nextSlideIndex);
        console.log('slideIndex+', slideIndex)
    }

    useEffect(() => {
      const interval = setInterval(() => {
        changeSlide();
        console.log('time')
      }, 10000);
      return () => {
        clearInterval(interval)
      };
    });


  return (
    <>
    <div className='slider_previous' key={previousSlide.id}>
      <img className='slider__img'  src={previousSlide.url} alt="slide img"/>
      <div className="slider__text-block">
        <h2 className="slider__title">ss</h2>
        <h2 className="slider__subtitle">ss</h2>
        <span className="slider__text">ss</span>
      </div>
      <a className="slider__arrow-link" href='/'>ss</a>
    </div>
        <div className='slider_current' key={currentSlide.id}>
        <img className='slider__img'  src={currentSlide.url} alt="slide img"/>
        <div className="slider__text-block">
          <h2 className="slider__title">ss</h2>
          <h2 className="slider__subtitle">ss</h2>
          <span className="slider__text">ss</span>
        </div>
        <a className="slider__arrow-link" href='/'>ss</a>
      </div>
      </>
  );
}
