import React, { useState } from "react";

function HeaderDropdown() {

    const headerMenu = [
        {
            title: 'О нас',
            link: '/'
        },
        {
            title: 'Портфолио',
            link: '/'
        },
        {
            title: 'Контакты',
            link: '/'
        },
        {
            title: 'Этапы проекта',
            link: '/'
        },
        {
            title: 'Заказать проект',
            link: '/'
        },
    ]


    return (
        <>
            <span
            className="header__dropdown-title"
            onMouseOver={() => setIsActive(true)}
            >Главная</span>
            <div className='header__dropdown-menu'>
                {headerMenu.map(i => 
                    <a className="header__dropdown-item" href={i.link}>{i.title}</a>
                )}
            </div>
        </>
    )
}

export default HeaderDropdown;
