import React, { useState } from "react";

function HeaderDropdown() {

    const [isActive, setIsActive] = useState(false);

    const headerMenu = [
        {
            title: 'О нас',
            link: '/'
        },
        {
            title: 'Портфолио',
            link: '/'
        },
        {
            title: 'Контакты',
            link: '/'
        },
        {
            title: 'Этапы проекта',
            link: '/'
        },
        {
            title: 'Заказать проект',
            link: '/'
        },
    ]


    return (
        <div onMouseLeave={() => setIsActive(false)}>
            <span
            className="header__dropdown-title"
            onMouseOver={() => setIsActive(true)}
            >Главная</span>
            <div className={`header__dropdown-menu ${isActive ? 'active':'' }`}>
                <li className="header__dropdown-item">О нас</li>
                <li className="header__dropdown-item">Портфолио</li>
                <li className="header__dropdown-item">Контакты</li>
                <li className="header__dropdown-item">Этапы проекта</li>
                <li className="header__dropdown-item">Заказать проект</li>
            </div>
        </div>
    )
}

export default HeaderDropdown;
